#include <iostream>
#include <vector>
#include <unordered_map>

using namespace std;

class Solution {
public:
    // return longest substring in s
    // such that each character appears at least k times
    int longestSubstring(string s, int k) {
        if (memoize.count(s)) return memoize[s];
        if (s.empty()) return 0;
        int maxLen = 0;
        vector<int> charCount(26);
        for (char c: s) {
            charCount[c-'a']++;
        }
        int i = 0, j = 0;
        while (i < s.length()) {
            while (i < s.length() && charCount[s[i]-'a'] < k) {
                i++;
            }
            if (i == s.length()) return maxLen;
            j = i;
            while (j < s.length() && charCount[s[j]-'a'] >= k) {
                j++;
            }
            if (j - i == s.length()) {
                maxLen = j - i; // special base case
            } else {
                maxLen = max(maxLen, longestSubstring(s.substr(i, j), k));
            }
            i = j;
        }
        return (memoize[s] = maxLen);
    }
private:

    // extra optimiztion in case of need
    unordered_map<string,int> memoize;
};

int main() {

    vector<tuple<int,string,int>> testCases = {
            {0, "", 100000},
            {2, "aabaa", 2},
            {3, "aaabb", 3},
            {5, "ababbc", 2}
    };

    Solution solution;
    for (auto& t: testCases) {
        cout << "expected: " << get<0>(t) << " actual: " << solution.longestSubstring(get<1>(t), get<2>(t)) << endl;
    }

    return 0;
}